package for_linked_list_lab1;//File: Node.java/**A class for Nodes used within a WCSLinkedList class. Contains basic constructors,access, and modifier methods.*/public class Node <E> {	/**	 * The data portion of a node	 */	private E myData;		/**	 * the Link of the next node	 */	private Node<E> myLink; 		/**	 * one parameter constructor that assigns the parameter to the data field and defaults	 *  the link field to null	 * @param theData - to be assigned to the data field	 */	public Node (E theData) {		myData = theData;		myLink = null;	}//one parameter constructor		/**	 * two parameter constructor that assigns its parameters to the two 	 * instance variable fields	 * @param theData- to be assigned to the data field	 * @param theLink- to be assigned to the link field.	 */	public Node (E theData, Node<E> theLink) {		myData = theData;		myLink = theLink;	}//2  parameter constructor		/**	 * Two parameter constructor that assigns its paameter to the two 	 * instance variable fields.	 * @param theLink to be assigned to the link field	 */	public void setLink(Node<E> theLink) {		myLink = theLink;	}//setLink		/**	 * modifier method for the Link field 	 * @param theData - to be assigned to the Link field	 */	public void setData(E theData) {		myData = theData;	}//getData		/**	 * access method for the link field	 * @return the link field of the Node<E>	 */	public Node<E> getLink(){		return myLink;	}//getLink		/**	 * access method for the link field	 * @return the data portion of the Node<E>	 */	public E getData() {		return myData;	}//getData	}//Node class